"use strict";angular.module("springboardApp",["ngAnimate","ngCookies","ngResource","ngRoute","ngSanitize","ngTouch"]).config(["$routeProvider",function(a){a.when("/",{templateUrl:"views/main.html",controller:"MainCtrl",controllerAs:"main"}).otherwise({redirectTo:"/"})}]),angular.module("springboardApp").controller("MainCtrl",["$scope","$http",function(a,b){b.get("//hackerearth.0x10.info/api/learning-paths?type=json&query=list_paths").then(function(b){a.apiData=b.data}),a.tagFilter=function(b){return a.searchText?b.tags.split(", ").indexOf(a.searchText)>=0:!0}}]),angular.module("springboardApp").run(["$templateCache",function(a){a.put("views/main.html",'<!-- toolbar --> <div class="row sb-toolbar"> <div class="search col-sm-12"> <input type="text" ng-model="searchText" placeholder="Search by tags"> </div> </div> <!-- feed --> <div class="row sb-feed"> <div class="col-sm-6 card" ng-repeat="path in apiData.paths | filter: tagFilter"> <div class="wrapper"> <div class="content"> <div class="image col-sm-4" style="background-image: url({{path.image}})"></div> <div class="data col-sm-8"> <h2 ng-bind="path.name"></h2> <p ng-bind="path.description"></p> <div class="row info"> <div class="col-xs-6 icon time"><span ng-bind="path.hours"></span> Hours</div> <div class="col-xs-6 icon user"><span ng-bind="path.learner"></span> Learners</div> </div> </div> </div> <a ng-href="{{path.sign_up}}" class="btn sb-btn" target="_blank">View Curriculum</a> </div> </div> </div>')}]);